import React, { Component } from 'react';
import { Consumer } from '../../context';
import ComponentLoading from '../layout/ComponentLoading';

class ArtistCard extends Component {
  render() {
    return (
    <Consumer>
        {value => {

            const {artist_info} = value;
            const {stats, image } = artist_info;

            if(stats === undefined || stats.length === 0) {
                return <ComponentLoading />
            } else {
                const {listeners, playcount} = stats;
                const mediumUserImageArray = image[3];
                const artistProfileImage = mediumUserImageArray[Object.keys(mediumUserImageArray)[0]]; 
                return (
                <React.Fragment>
                    <section className="user-card">
                        <div className="grid-row">
                        <div className="grid-cell">
                        <div className="user-card-wrapper">
                        <div className="user">
                        {/* <img src={artistProfileImage} /> */}
                        </div>
                         <p>Listeners: {listeners} </p>
                         <p>plays : {playcount}</p>
                        </div>
                        </div>
                        </div>
                    </section>

                </React.Fragment>
                )
            }
        }}
    </Consumer>
    )
  }
}
export default ArtistCard;
