import React, { Component } from 'react'
import axios from 'axios';
import { Consumer } from '../../context';
import ComponentLoading from '../layout/ComponentLoading';
import TrackListItem from '../tracks/TrackListItem';
class ArtistProfile extends Component {


  render() {
    return (
      <Consumer>
        {value => {

            const {artist_top_tracks} = value;
            if(artist_top_tracks === undefined || artist_top_tracks.length === 0) {
                return <ComponentLoading />
            } else {

                return (
                <React.Fragment>
                 <p>artists</p>
                 <ul className="track-list">
                      {artist_top_tracks.map(item => (    
                        <TrackListItem key={item.mbid} track={item}/>
                      ))}
                    </ul>  
                </React.Fragment>
                )
            }
        }}
    </Consumer>
    )
  }
}
export default ArtistProfile
