import React, { Component } from 'react';
import { Consumer } from '../../context';
import ComponentLoading from '../layout/ComponentLoading';
import axios from 'axios';
// const css = require('./UserProfile.scss');

class ArtistProfileCard extends Component {


  render() {
    return (
    <Consumer>
        {value => {

            const {artist_info} = value;
            const { name, ontour } = artist_info;

            //TODO fix the on tour
            if(artist_info === undefined || artist_info.length === 0) {
                return <ComponentLoading />
            } else {

                return (
                <React.Fragment>
                   <section className="profile-banner">
                        <div className="grid-container">
                            <div className="grid-row justify-content-end">
                                <div className="grid-cell cell-width-8">
                                    <p className="name">{name}</p>
                                    <span className="on-tour">
                                    {{ontour} === '1' &&
                                        <p>ontour</p>
                                    }
                                    </span>
                                </div>
                            </div>
                        </div>
                    </section>
                </React.Fragment>
                )
            }
        }}
    </Consumer>
    )
  }
}
export default ArtistProfileCard;
