import React, { Component } from 'react'

class AlbumListItem extends Component {

    constructor(props) {
        super(props);
    }

  render() {
    const images = this.props.album.image;
    const mediumImageArray = images[3];
    const albumImage = mediumImageArray[Object.keys(mediumImageArray)[0]]; 

    const backgroundImage ={
      backgroundImage: 'url("'+albumImage+'")'
    };
    return (
      <li className="top-album" >
      <div className="item-info">
        <span className="album"> {this.props.album.name}   </span> 
        <span>{this.props.album.artist.name}</span>
        <span>{this.props.album.playcount} Plays</span>
      </div>

        <img src={albumImage}/>
      </li>
    )
  }
}

export default AlbumListItem;
