import React, { Component, Fragment } from 'react'
import { Consumer } from '../../context';
import ArtistTracks from './ArtistTracks';
import ArtistProfile from './ArtistProfile';
import ArtistCard from './ArtistCard';
import ContentBlurb from '../layout/ContentBlurb';
import axios from 'axios';

class ArtistWrapper extends Component {
    componentDidMount() {
        const {dispatch} = this.props.value;
        this.getArtistTopTracks(dispatch);
        this.getArtistInfo(dispatch);
      }
      getArtistTopTracks = (dispatch) => {
        axios.get(`http://ws.audioscrobbler.com/2.0/?method=artist.gettoptracks&limit=10&artist=${this.props.value.user}&api_key=56e994f5d0222486f464e9662e9eb941&format=json`)
        .then(res => {
          dispatch({
            type: 'UPDATE_ARTIST_TOP_TRACKS',
            payload: res.data.toptracks.track
          });
          //put the new results into the context state, like a reducer in redux
        })
        .catch(err => {
            // request failed component
        })
      }

    getArtistInfo = (dispatch) => {
        axios.get(`http://ws.audioscrobbler.com/2.0/?method=artist.getInfo&artist=${this.props.value.user}&api_key=56e994f5d0222486f464e9662e9eb941&format=json`)
        .then(res => {
          dispatch({
            type: 'UPDATE_ARTIST_INFO',
            payload: res.data.artist
          });
          //put the new results into the context state, like a reducer in redux
        })
        .catch(err => {
            // request failed component
        })
      }
  render() {
    return (

      <Consumer>
          {value => {

             return (
                <Fragment>
                <ArtistProfile value={value} />
                <div className="grid-container">
                <div className="grid-row">
                <div className="grid-cell cell-width-4">
                   <ArtistCard value={value}/>
                </div>
                <div className="grid-cell cell-width-8">
                {value.artist_info.bio != undefined && 
                  <ContentBlurb content={value.artist_info.bio.summary} />
                }
                <ArtistTracks value={value} />
                </div>
                </div>
                </div>
              </Fragment>
             )
          }}
      </Consumer>


    )
  }
}
export default ArtistWrapper
