import React, { Component } from 'react'

class ContentBlurb extends Component {
    constructor(props) {
        super(props);
    }
    createMarkup = () => {
        return {__html: this.props.content};
    }
    render() {
        return (
            <div dangerouslySetInnerHTML={this.createMarkup()} />
        )
  }
}

export default ContentBlurb;
