import React, { Component } from 'react';
import { Consumer } from '../../context';
import ComponentLoading from '../layout/ComponentLoading';
const css = require('./UserProfile.scss');

class UserProfile extends Component {
  render() {
    return (
    <Consumer>
        {value => {

            const {userInfo} = value;

            if(userInfo === undefined || userInfo.length === 0) {
                return <ComponentLoading />
            } else {
                const {realname, country} = userInfo;

                return (
                <React.Fragment>
                   <section className="profile-banner">
                        <div className="grid-container">
                            <div className="grid-row justify-content-end">
                                <div className="grid-cell cell-width-8">
                                    <p className="name">{realname}</p>
                                    <span className="location">{country}</span>
                                </div>
                            </div>
                        </div>
                    </section>
                </React.Fragment>
                )
            }
        }}
    </Consumer>
    )
  }
}
export default UserProfile;
