import React, { Component } from 'react';
import { Consumer } from '../../context';
import ComponentLoading from '../layout/ComponentLoading';
const userCardCss = require('./UserCard.scss');

class UserCard extends Component {
  render() {
    return (
    <Consumer>
        {value => {

            const {userInfo} = value;

            if(userInfo === undefined || userInfo.length === 0) {
                return <ComponentLoading />
            } else {
                const {realname, playcount, image} = userInfo;
                const mediumUserImageArray = image[3];
                const userProfileImage = mediumUserImageArray[Object.keys(mediumUserImageArray)[1]]; 
                return (
                <React.Fragment>
                    <section className="user-card">
                        <div className="grid-row">
                        <div className="grid-cell">
                        <div className="user-card-wrapper">
                        <div className="user">
                        <img src={userProfileImage} />
                        </div>

                         <p>PLAYCOUNT :{playcount}</p>
                         <p>ARTISTS: 500 </p>
                         <p>FOLLOWERS : 120</p>
                         <button>Follow {realname}</button>
                        </div>

                        </div>
                        </div>
                    </section>

                </React.Fragment>
                )
            }
        }}
    </Consumer>
    )
  }
}
export default UserCard;
